import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

const AjouterSt = (props) => {
    const [NouveauStg, SetNouveauStg] = useState({
        image: '',
        nom: '',
        prenom: '',
        email: '',
    });

    const navigate = useNavigate();
    const [ajoutEffectue, setAjoutEffectue] = useState(false);

    useEffect(() => {
        if (ajoutEffectue) {
            // Rediriger une fois l'ajout effectué
            navigate("/");
        }
    }, [ajoutEffectue, navigate]);

    function ajouterStg() {
        // Éviter les re-rendus inutiles
        if (!ajoutEffectue) {
            props.ajouterstg(NouveauStg);
            setAjoutEffectue(true);
        }
    }

    return (
        <>
            <form>
                {/* ... Votre formulaire */}
                <button type='button' onClick={ajouterStg}>Ajouter</button>
            </form>
        </>
    );
}

export default AjouterSt;
