import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const ListStg = (props) => {
    const [liststg,setliststg]=useState([])
    const [id,setId]=useState(0)

 
        function modifier(image,id,nom,prenom,email){
            props.modifierstg(image,id,nom,prenom,email)
        }
    return (
        <div>
            <h1>Liste Des stagiaires</h1>
            <Link to="/AjouterStagiaire"><button type='button' >Ajouter Stagiaire</button></Link>
            <table>
                <tr>
                    <th>id :</th>
                    <th>Image</th>
                    <th>nom</th>
                    <th>prenom</th>
                    <th>email</th>
                </tr>
                {props.liststg.map((e)=>
                    <tr key={e.id}>
                        <td> id: {e.id}</td>
                        <td><img src={`/${e.image}`} alt="photo" style={{width:"30px"}}/> </td>
                        <td>{e.nom}</td>
                        <td>{e.prenom}</td>
                        <td>{e.email}</td>
                        <Link to ={`/ModifierStg/${e.id}`} onClick={()=>modifier(e.id,e.image,e.nom,e.prenom,e.email)}><button type='modifier'>Modifier</button></Link>   
                        <Link to =""><button type='supprimer'>Supprimer</button></Link>
                    </tr>
                )}

            </table>
        </div>
    );
}

export default ListStg;
