import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const AjouterStg = (props) => {
    const [nouveauStg,setnouveauStg]=useState({image:"",nom:"",prenom:"",email:""})

    //function ajouter(){ 
        //setnouveauStg({id:setid(id+1),...nouveauStg})
       // props.ajouterstg(nouveauStg)

   // }
   function ajouter() {
        props.ajouterstg({ ...nouveauStg, id: props.id }); // Use the id prop here
    }

    return (
        <>
            <form>
                <fieldset>
                    <label> Image : </label>
                    <input  type='text' id="image" value={nouveauStg.image} onChange={(e)=>setnouveauStg({...nouveauStg,image:e.target.value})}/><br/>
                    <label>Nom : </label>
                    <input  type='text' id="nom"  value={nouveauStg.nom}  onChange={(e)=>setnouveauStg({...nouveauStg,nom:e.target.value})}/><br/>
                    <label>Prenom : </label>
                    <input  type='text' id="prenom"  value={nouveauStg.image}  onChange={(e)=>setnouveauStg({...nouveauStg,prenom:e.target.value})}/><br/>
                    <label> Email</label>
                    <input  type='text' id="email"  onChange={(e)=>setnouveauStg({...nouveauStg,email:e.target.value})}/><br/>
                    <Link to="/"><button type='button' onClick={ajouter}>Ajouter</button></Link>
                </fieldset>
            </form>    
        </>
    );
}

export default AjouterStg;
