import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

const AjouterSt = (props) => {
    const [NouveauStg, SetNouveauStg] = useState({
        image: '',
        nom: '',
        prenom: '',
        email: '',
    });

    const navigate = useNavigate();

    useEffect(() => {
        // Si props.ListStg est fourni (pour la modification), initialisez NouveauStg avec ces valeurs
        if (props.ListStg) {
            SetNouveauStg({
                image: props.ListStg.image,
                nom: props.ListStg.nom,
                prenom: props.ListStg.prenom,
                email: props.ListStg.email,
            });
        }
    }, [props.ListStg]);

    function ajouterStg() {
        props.ajouterstg(NouveauStg);
        navigate("/");
    }

    function modifier() {
        // Utilisez props.modifierstg pour mettre à jour les données du stagiaire
        props.modifier(NouveauStg.image, props.id, NouveauStg.nom, NouveauStg.prenom, NouveauStg.email);
        navigate("/");
    }

    return (
        <>
            <form>
                <fieldset>
                    <label>Image:</label>
                    <input type='text' id='image' onChange={(e) => SetNouveauStg({ ...NouveauStg, image: e.target.value })} value={NouveauStg.image} /><br />
                    <label>Nom:</label>
                    <input type='text' id='nom' onChange={(e) => SetNouveauStg({ ...NouveauStg, nom: e.target.value })} value={NouveauStg.nom} /><br />
                    <label>Prenom:</label>
                    <input type='text' id='prenom' onChange={(e) => SetNouveauStg({ ...NouveauStg, prenom: e.target.value })} value={NouveauStg.prenom} /><br />
                    <label>Email:</label>
                    <input type='text' id='email' onChange={(e) => SetNouveauStg({ ...NouveauStg, email: e.target.value })} value={NouveauStg.email} /><br />
                    {props.modifierstg ? (
                        <button type='button' onClick={modifier}>Modifier</button>
                    ) : (
                        <button type='button' onClick={ajouterStg}>Ajouter</button>
                    )}
                </fieldset>
            </form>
        </>
    );
}

export default AjouterSt;
