import React from 'react';

import { Link } from 'react-router-dom';

const ListStagiaire = (props) => {
    function supprimerStg(i){
        props.supprimerstg(i)
    }
    function modifierstg(){

    }
    console.log(props.ListStg)
    return (
        <>
            <main className="container"> 
                <h1>List Des Stagiaires </h1> 
                 <Link to="/StagiaireAjouter"> <button type='button'> Ajouter Stagiaire</button></Link>
                <div className="row mb-2">
                {
                     props.ListStg.map((e)=>
                        
                     <div className="col"> 
                     <div className="card shadow-sm"> 
                         <img className="bd-placeholder-img card-img-top" 
                         src={`/imgs prjt/${e.image}`} alt="photo" /> 
                         <div className="card-body">
                             <p className="card-text" > id : {e.id}</p> 
                             <p className="card-title"> Nom et Prenom : {e.prenom} {e.nom} </p> 
                             <p className="card-text"> Email : {e.email}</p> 
                             <div className="d-flex justify-content-between align- 
                             items-center"> 
                                 <div className="btn-group"> 
                                <Link to="/"> <button type="button" className="btn btn1 btn-sm btn- 
                                     outline-secondary"onClick={()=>{modifierstg(e.id)}}> Modifier </button> </Link>
                                  <Link to="/"><button type="button" onClick={()=>{supprimerStg(e.id,e.email,e.nom,e.prenom.e.image)}} className="btn btn1 btn-sm btn- 
                                     outline-secondary">Supprimer</button> </Link>
                                 </div> 
                             </div> 
                         </div> 
                     </div> 
                 </div>
                )}
                </div> 
            </main> 
        </>
    );
}

export default ListStagiaire;
