import React, { useState } from 'react';
import { BrowserRouter, Route, Routes } from 'react-router-dom';
import ListStg from './ListStg';
import AjouterStg from './AjouterStg';

const AppF = () => {
  const [id,setid]=useState(0)
    const [liststg,setliststg]=useState([])
   // function ajouterstg(nouveauStg){
      //  setliststg([...liststg,nouveauStg])
   // }
    function ajouterstg(nouveauStg) {
      setliststg([...liststg, { ...nouveauStg, id }]);
      setId(id + 1); // Update id here
  }
    return (
        <>
          <BrowserRouter>
            <Routes>
                <Route path='/' element={<ListStg liststg={liststg} />}/>
                <Route path='/AjouterStagiaire' element={<AjouterStg ajouterstg={ajouterstg} />}/>
                
                
            </Routes>
          </BrowserRouter>  
        </>
    );
}

export default AppF;
