import React, { useState } from 'react';
import { BrowserRouter, Route, Routes } from 'react-router-dom';
import ListStagiaire from './ListStagiaire';
import AjouterSt from './AjouterSt';

const CheminF = () => {
    const [listStg,SetlistStg]=useState([])
    const [id,setId]=useState(1)
    function ajouterstg(nouveauStg) {
        SetlistStg([...listStg, { ...nouveauStg, id: id }]);
        setId(id + 1);
      }
    const supprimerstg=(id)=>{
        const supp=listStg.filter((s)=>s.id !==id)
        SetlistStg(supp)
    }
    const modifier=(image,id,nom,prenom,email)=>{
        const nouveaustg=listStg.map((e)=>e.id==id ? SetlistStg([...listStg,{eimage,id,nom,prenom,email} ]) :null);
        SetlistStg([...listStg, nouveaustg])
    }
    return (
        <>
            <BrowserRouter>
                <Routes>
                <Route path='/' element={<ListStagiaire supprimerstg={supprimerstg} ListStg={listStg} modifier={modifier} />} />
                    <Route path='/StagiaireAjouter' element={<AjouterSt ajouterstg={ajouterstg} ListStg={listStg} />} />
                    <Route path='/ModifierStg/:id' element={<AjouterSt ajouterstg={ajouterstg} ListStg={listStg} modifier={modifier} />} />
                </Routes>
            </BrowserRouter>
        </>
    );
}

export default CheminF;
