import React, { useState } from 'react';
import { BrowserRouter, Route, Routes } from 'react-router-dom';
import ListStagiaire from './ListStagiaire';
import ModifierSt from './ModifierSt';
import SupprimerSt from './SupprimerSt';
import AjouterSt from './AjouterSt';

const CheminF = () => {
    const [listStg,SetlistStg]=useState([
        {
            id:1,
            nom:"chaimae",
            prenom:"chaire",
            email:"chaimae@gmail.com",
            image:"femme1.PNG"
        },
        {
            id:2,
            nom:"aya",
            prenom:"sendi",
            email:"aya@gmail.com",
            image:"femme2.PNG"
        }
    ])
    const [id,setId]=useState(0)
    function ajouterstg(nouveauStg) {
        SetlistStg([...listStg, { ...nouveauStg, id: id }]);
        setId(id + 1);
      }
    const supprimerstg=(id)=>{
        const supp=listStg.filter((s)=>s.id !==id)
        SetlistStg(supp)
    }
    const modifier=(image,id,nom,prenom,email)=>{
        const nouveaustg=listStg.map((e)=>
            e.id==id ? {image,id,nom,prenom,email}:listStg
        );
        SetlistStg(nouveaustg)
    }
    return (
        <>
            <BrowserRouter>
                <Routes>
                    <Route path='/' element={<ListStagiaire supprimerstg={supprimerstg} ListStg={listStg}/>} />
                    <Route path='/StagiaireAjouter' element={<AjouterSt ajouter={ajouterstg} ListStg={listStg} />} />
                    <Route path='/' element={<ListStagiaire  supprimerstg={supprimerstg} ListStg={listStg}/>} />
                    <Route path='/StagiaireAjouter' element={<AjouterSt modifierstg={modifier} ListStg={listStg} />} />
                </Routes>
            </BrowserRouter>
        </>
    );
}

export default CheminF;
