import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const AjouterSt = (props) => {
    const [NouveauStg,SetNouveauStg]=useState({})
    
    const navigate=useNavigate()
    function ajouterStg(){
        props.ajouterstg({ ...NouveauStg, id: props.id });
        navigate("/")
    }
    
    function modifierStg() {
        // Utilisez props.modifierstg pour mettre à jour les données du stagiaire
        props.modifierstg(NouveauStg.image, props.id, NouveauStg.nom, NouveauStg.prenom, NouveauStg.email);
        navigate("/");
    }
    return (
        <>
            <form>
                <fieldset>
                    <label> Image  : </label>
                    <input type='text' id='image'onChange={(e)=>SetNouveauStg({...NouveauStg,image: e.target.value})}value={props.ListStg.image} /><br/>
                    <label> Nom : </label>
                    <input type='text' id='nom' onChange={(e)=>SetNouveauStg({...NouveauStg,nom: e.target.value})} value={props.ListStg.nom}/><br/>
                    <label> Prenom : </label>
                    <input type='text' id='prenom' onChange={(e)=>SetNouveauStg({...NouveauStg,prenom: e.target.value})} value={props.ListStg.prenom}/><br/>
                    <label>Email : </label>
                    <input type='text' id='email' onChange={(e)=>SetNouveauStg({...NouveauStg,email: e.target.value})}value={props.ListStg.email}/><br/>
                    <button type='button' onClick={ajouterStg}> Ajouter </button>
                </fieldset>
            </form>
        </>
    );
}
