import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const AjouterStg = () => {
    const [nouveauStg,setnouveauStg]=useState({image:"",nom:""})
    const [id,setid]=useState(0)
    function ajouter(props){
        props.ajouterstg(nouveauStg)
        setnouveauStg({id:setid(id+1),...nouveauStg})
    }
    return (
        <>
            <form>
                <fieldset>
                    <label> Image : </label>
                    <input  type='text' id="image" onChange={(e)=>setnouveauStg({...nouveauStg,image:e.target.value})}/><br/>
                    <label>Nom : </label>
                    <input  type='text' id="nom" onChange={(e)=>setnouveauStg({...nouveauStg,nom:e.target.value})}/><br/>
                    <label>Prenom : </label>
                    <input  type='text' id="prenom" onChange={(e)=>setnouveauStg({...nouveauStg,prenom:e.target.value})}/><br/>
                    <label> Email</label>
                    <input  type='text' id="email"  onChange={(e)=>setnouveauStg({...nouveauStg,email:e.target.value})}/><br/>
                    <Link to="/"><button type='button' onClick={ajouter}>Ajouter</button></Link>
                </fieldset>
            </form>    
        </>
    );
}

export default AjouterStg;
