import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import "./style.css"

const AjouterSt = (props) => {
    const [NouveauStg, SetNouveauStg] = useState({ image: '', nom: '', prenom: '', email: '' });
    const navigate = useNavigate();
    
    function ajouterStg() {
        props.ajouterstg(NouveauStg);
        navigate("/");
    }

    function modifier() {
        props.modifier(NouveauStg.image, props.id, NouveauStg.nom, NouveauStg.prenom, NouveauStg.email);
        navigate("/");
    }

    return (
        <>
            <form>
                <fieldset>
                    <h3 className='center2'>Ajouter Stagiaire</h3>
                    <label>Image :</label>
                    <input type='text' id='image' value={NouveauStg.image} onChange={(e) => SetNouveauStg({ ...NouveauStg, image: e.target.value })} value={NouveauStg.image} /><br/>
                    <label >Nom :</label>
                    <input type='text' id='nom' value={NouveauStg.nom} onChange={(e) => SetNouveauStg({ ...NouveauStg, nom: e.target.value })} value={NouveauStg.nom} /><br/>
                    <label >Prenom :</label>
                    <input type='text' id='prenom' value={NouveauStg.prenom} onChange={(e) => SetNouveauStg({ ...NouveauStg, prenom: e.target.value })} value={NouveauStg.prenom} /><br/>
                    <label >Email :</label>
                    <input type='text' id='email'  value={NouveauStg.email} onChange={(e) => SetNouveauStg({ ...NouveauStg, email: e.target.value })} value={NouveauStg.email} /><br/>
                    {props.modifier ? (
                        <button type='button' onClick={modifier} className='butn but2'>Modifier</button>
                    ) : (
                        <button type='button' onClick={ajouterStg} className='butn but2'>Ajouter</button>
                    )}
                </fieldset>
            </form>
        </>
    );
}

export default AjouterSt;
